import React , {useReducer } from 'react'
import AddForm from './AddForm'
import Control from './Control'
import EditForm from './EditForm'
import Items from './Items'


export default function ImageEditor({items}) {
    const init_state = {items:items,addForm:0,editForm:0,obj:{}}
    const reducer =(state,action)=>{
        switch(action.type){
            case 'add':
                return {...state,items:[...state.items,action.payload.e]}
            case 'delete':
                return {...state,items:state.items.filter(item=>item!==action.payload.e)}
            case 'edit':
                return {}
            case 'addForm':
                return {...state,addForm:action.payload.val}
            case 'editForm':
                    if(action.payload.val===0){
                        const obj = action.payload.obj.title
                        obj['br'] = action.payload.data.br
                        let i = state.items.indexOf(action.payload.obj.title)
                        let T = state.items
                        T.splice(i,1,{...obj})
                    }
                return {...state,editForm:action.payload.val,obj:action.payload.obj,items:[T]}
            default:
                return {items:[]}
        }
    }
    const [state,dispatch] = useReducer(reducer,init_state)

    return (
        <div className='container'>
            {console.log('Container render')}
            <header className='border p-2 d-flex justify-content-between align-items-center'>
                <h1 className="logo col-3 text-center">imageEditor</h1>
                <i title='profile' className="fa-solid fa-user h1 col-2 text-center"></i>
            </header>
            <div className="body d-flex">
                <Control dispatch={dispatch}/>
                <Items items={state.items} dispatch={dispatch}/>
            </div>
            {state.addForm===1&&<AddForm dispatch={dispatch}/>}
            {state.editForm===1&&<EditForm dispatch={dispatch} obj={state.obj}/>}
        </div>
    )
}
