import React , { useState } from 'react'
import { useRef } from 'react'

export default function AddForm({dispatch}) {
    const [img,setImg] = useState(null)
    const title = useRef('')

    const createUrl=(val)=>{
        let url = URL.createObjectURL(val)
        let img = new Image()
        img.src =  
        console.log('img',url)
        setImg(url)
    }
    const addItem=()=>{
        dispatch(
            {type:'add',payload:{e:
                {...{title:title.current,images:[img]}}
            }}
        )
        dispatch({type:'addForm',payload:{val:0}})
    }
    return (
        <div className='addFormP'>
            <div className="addForm border p-4 col-4 bg-light d-flex flex-column align-items-center justify-content-evenly">
                <h1 className='col-12 my-2'>AddForm</h1>
                <div className="form-floating col-12 my-2">
                    <input type="text" onChange={(event)=>{title.current=event.target.value}} className="form-control" placeholder='title'/>
                    <label>title</label>
                </div>
                <input onChange={(event)=>createUrl(event.target.value)} className='my-2' type="file" />
                <img style={{width:'300px',height:'20vh'}} src={(img!==null?img.href:'')} alt="" />
                <button onClick={()=>addItem()} className='btn btn-success col-6 p-2 my-2'>Add</button>
            </div>
        </div>
    )
}
