import axios from "axios";
import React, {useEffect, useReducer , Provider } from "react";
import ImageEditor from "./Component/ImageEditor";
import './Css/style.css';

export const AppContext = React.createContext()
const init_state = {items:[],cmp:0}
const reducer =(state,action)=>{
    switch(action.type){
            case 'dispaly':
            return {...state,cmp:1,items:[...state.items,action.payload.e]}                
        case 'add':
            return {...state,items:[...state.items,action.payload.e]}
        default:
            return {items:[]}
    }
}

export default function App(){
    const [state,dispatch] = useReducer(reducer,init_state)
    useEffect(()=>{
        axios.get('https://dummyjson.com/users')
        .then(res=>{
            console.log(res.data);dispatch({type:'display',payload:{e:res.data}});
        }).catch(err=>console.log('error :' , err.message))
    },[])
    
    return(
        <AppContext.Provider value={{dispatch,state}}>
        </AppContext.Provider>
    )
}
